void Continue()
{
    while(1)
    {
       if (Step() != OK) return;
    }
}

int main()
{
    InitCPU();
    InitEmulator();
    GraphicsInit();
    EnableInterpreter();
    FillKeyboardBufferString("?EGA ON STARTER\nMOUNTB\n");
    Continue();

    int idx = 0;
    for(;;)
    {
        if (planetseeds[idx].species == -1) return 0;
        char dummy[128];
        sprintf(dummy, "mountb\n%i\nmercator-gen\n", planetseeds[idx].seed);

        FillKeyboardBufferString(dummy);
        Continue();

        if (planetseeds[idx].species == 18)
            memcpy(&m[0x7e51<<4], earthmap, 24*48);

        sprintf(dummy, OUTDIR"/maps/planet_%i_%i_%i.ppm",
            planetseeds[idx].x/8,
            planetseeds[idx].y/8,
            planetseeds[idx].orbit);

            int clrmap = 0;
            switch(planets[planetseeds[idx].species].surftype)
            {
              case 4: clrmap = 4; break;
              case 1: clrmap = 5; break;
              case 3: clrmap = 3; break;
              default:
                if ((planets[planetseeds[idx].species].lseed&0xFF) != 0)
                  clrmap = 1;
                else
                  clrmap = 2;
              break;
            }
        //char* palette = &cmap[planets[planetseeds[idx].species].surftype+9][0];
        char* palette = &cmap[clrmap + 9][0];
        //char* palette = &cmap[10][0];

        FILE *fp = fopen(dummy, "wb");
        fprintf(fp, "P3\n48 48\n255\n");
        for(int j=23; j>=0; j--)
        for(int i=0; i<48; i++)
        {
            int c = ((int)((signed char)Read8Long(0x7e51, j*48+i)))+128;
            fprintf(fp, "%i %i %i ", c, c, c);
        }
        //for(int i=48*24-1; i>=0; i--)
        for(int j=23; j>=0; j--)
        for(int i=0; i<48; i++)
        {
            int c = ((int)((signed char)Read8Long(0x7e51, j*48+i)));
            c = c<0?0:((c >> 1) & 0x38);
            c = palette[c] & 0xF;
            fprintf(fp, "%i %i %i ", (colortable[c]>>16)&0xFF, (colortable[c]>>8)&0xFF, (colortable[c]>>0)&0xFF);
        }
        fprintf(fp, "\n");
        fclose(fp);

        idx++;
    }

    return 0;
}
